binding.script:
var TrimVal = getprop("/controls/flight/rudder-trim");
setprop("/controls/flight/rudder-trim", TrimVal + 0.01);
setprop("/controls/switches/rudder-trim", 1);

var TrimVal = getprop("/controls/flight/rudder-trim");
setprop("/controls/flight/rudder-trim", TrimVal - 0.01);
setprop("/controls/switches/rudder-trim", -1);

if (getprop("/it-autoflight/output/vert") != 1 and getprop("/it-autoflight/custom/trk-fpa") == 0) {
	setprop("/it-autoflight/input/vert", 1);
} else if (getprop("/it-autoflight/output/vert") != 5 and getprop("/it-autoflight/custom/trk-fpa") == 1) {
	setprop("/it-autoflight/input/vert", 5);
}

if (getprop("/it-autoflight/output/vert") != 1 and getprop("/it-autoflight/custom/trk-fpa") == 0) {
	setprop("/it-autoflight/input/vert", 1);
} else if (getprop("/it-autoflight/output/vert") != 5 and getprop("/it-autoflight/custom/trk-fpa") == 1) {
	setprop("/it-autoflight/input/vert", 5);
}

var spdBrk = getprop("/controls/flight/speedbrake-lever" );
if(spdBrk == 0){
	setprop("/controls/flight/speedbrake-arm", 0);
	setprop("/controls/flight/speedbrake-lever", 0.5);
}
elsif(spdBrk == 0.5){
	setprop("/controls/flight/speedbrake-arm", 0);
	setprop("/controls/flight/speedbrake-lever", 1);
}
elsif(spdBrk == 1){
	setprop("/controls/flight/speedbrake-arm", 0);
	# Do nothing because spoilers are fully up.
}

var spdBrk = getprop("/controls/flight/speedbrake-lever");
if(spdBrk == 0){
	setprop("/controls/flight/speedbrake-arm", 0);
	# Do nothing because spoilers are fully down.
}
elsif(spdBrk == 0.5){
	setprop("/controls/flight/speedbrake-arm", 0);
	setprop("/controls/flight/speedbrake-lever", 0);
}
elsif(spdBrk == 1){
	setprop("/controls/flight/speedbrake-arm", 0);
	setprop("/controls/flight/speedbrake-lever", 0.5);
}

var a = getprop("/controls/flight/rudder");
a += 0.1;
if (a > 1) a = 1;
setprop("/controls/flight/rudder", a);

var a = getprop("/controls/flight/rudder");
a -= 0.1;
if (abs(a) > 1) a = -1;
setprop("/controls/flight/rudder", a);
